import React, { useState } from "react";
import Navbar from "./Navbar";
import Weather from "./Weather";
import "../styles.css";

function App() {
  const [showLogin, setShowLogin] = useState(true);
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [signupData, setSignupData] = useState({
    username: "",
    email: "",
    password: "",
  });
  const [username, setUsername] = useState(
    localStorage.getItem("username") || ""
  );
  const [authenticated, setAuthenticated] = useState(
    !!localStorage.getItem("username")
  ); // Initialize authenticated state based on localStorage
  const [showPassword, setShowPassword] = useState(false); // Add state for password visibility

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSignupChange = (e) => {
    const { name, value } = e.target;
    setSignupData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    console.log("Login data:", loginData);
    // Simulate successful login
    const { username } = loginData;
    setUsername(username); // Set the username in state
    localStorage.setItem("username", username); // Store the username in LocalStorage
    setAuthenticated(true); // Set authenticated to true
    setShowLogin(false); // Hide the login form
  };

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    console.log("Signup data:", signupData);
    // Simulate successful signup
    const { username } = signupData;
    setUsername(username); // Set the username in state
    localStorage.setItem("username", username); // Store the username in LocalStorage
    setAuthenticated(true); // Set authenticated to true
    setShowLogin(false); // Hide the login form
  };

  const togglePasswordVisibility = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const logout = () => {
    // Clear username from state and LocalStorage
    setUsername("");
    localStorage.removeItem("username");
    setAuthenticated(false); // Set authenticated to false
  };

  return (
    <div className="App">
      <Navbar username={username} onLogout={logout} />

      {authenticated ? (
        <Weather />
      ) : (
        <div>
          {showLogin ? (
            <div className="login-container">
              <h2>Login</h2>
              <form onSubmit={handleLoginSubmit}>
                <input
                  type="text"
                  name="username"
                  placeholder="Username"
                  value={loginData.username}
                  onChange={handleLoginChange}
                />
                <input
                  type={showPassword ? "text" : "password"} // Toggle input type based on showPassword state
                  name="password"
                  placeholder="Password"
                  value={loginData.password}
                  onChange={handleLoginChange}
                />
                <button type="button" onClick={togglePasswordVisibility}>
                  {showPassword ? "Hide" : "Show"} Password
                </button>
                <button type="submit">Login</button>
              </form>
            </div>
          ) : (
            <div className="signup-container">
              <h2>Sign Up</h2>
              <form onSubmit={handleSignupSubmit}>
                <input
                  type="text"
                  name="username"
                  placeholder="Username"
                  value={signupData.username}
                  onChange={handleSignupChange}
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={signupData.email}
                  onChange={handleSignupChange}
                />
                <input
                  type={showPassword ? "text" : "password"} // Toggle input type based on showPassword state
                  name="password"
                  placeholder="Password"
                  value={signupData.password}
                  onChange={handleSignupChange}
                />
                <button
                  type="button"
                  onClick={togglePasswordVisibility}
                  className="password-visibility-btn"
                >
                  {showPassword ? "Hide" : "Show"} Password
                </button>
                <button type="submit">Sign Up</button>
              </form>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
